```cpp
#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
const int N=2e6+10;
ll n,m,a[N];
int p[N],tail;
void add(int v)
{
			p[++tail]=v;
}
inline ll read()
{
        ll x=0,f=1;
        char ch=getchar();
        while(ch<'0'||ch>'9'){
            if(ch=='-')
                f=-1;
            ch=getchar();
        }
