            x=(x<<1)+(x<<3)+(ch^48);
            ch=getchar();
        }
        return x*f;
}
int sum(int x,ll y)
{
        int i,v,maxi;
        ll maxn=0;
        for(i=1;i<=tail;i++)
        {
            v=p[i];
            if(maxn<=a[v])
            {
                if(maxn==a[v]){
                    maxi=min(v,maxi);
                }
                else maxi=v;
                maxn=a[v];
            }
