			}
		}
		for(int i=head[ji];i;i=edge[i].next)
		{
			if(maxn1<a[edge[i].to]) maxn1=a[edge[i].to],ji2=edge[i].to;
			if(maxn1==a[edge[i].to]) 
			{	if(ji2>edge[i].to)
				ji2=edge[i].to; 
			} 
		}
		int cha=maxn-maxn1;
		if(n==1)
		cout<<"1"<<endl;
		else if((m-cha)<0) 
		printf("%lld\n",ji);
		else if((m-cha)==0) 
		{
		if(ji>ji2)
		{ printf("%lld\n",ji2);
		}
		else printf("%lld\n",ji);
		}
		else {
			if(ji>ji2){
				if((m-cha)%2==0) 
				printf("%lld\n",ji2);
				else 
				printf("%lld\n",ji);
				}
			else if(ji<ji2){
				if((m-cha)%2==0) 
				printf("%lld\n",ji);
				else 
				printf("%lld\n",ji2);
			}
		}

	}
	return 0;
}
