			if(fl){
				if(!cnt){
					ans[cnt|qwq]=nw;ans[cnt|(qwq^1)]=i;cnt+=2;
					continue;
				}
				cout<<nw<<' '<<ans[cnt-1]<<endl;
				cin>>op;fl=0;qwq=op^1;
				ans[cnt|qwq]=nw;ans[cnt|(qwq^1)]=i;cnt+=2;
				continue;
			}
			ans[cnt|qwq]=nw;ans[cnt|(qwq^1)]=i;cnt+=2;
