				i+=2;awa=0;continue;
			}
			cout<<qaq.top()<<' '<<ans[i]<<endl;
			cin>>op;
			if(!op){
				i+=2;awa=0;continue;
			}
			cout<<qaq.top()<<' '<<ans[i-1]<<endl;
			cin>>op;
			if(!op){
				i++;awa=1;continue;
			}
			kk[c]=make_pair(qaq.top(),i);c++;qaq.pop();
			i++;awa=1;
		}
		if(!qaq.empty()){
			cout<<qaq.top()<<' '<<ans[cnt-1]<<endl;
			cin>>op;
			if(op==1){
				kk[c]=make_pair(qaq.top(),cnt);c++;qaq.pop();
			}
		}
		if(!qaq.empty()) wr(-1);
		else wr(0);
	}
	return 0;
}
