int n;
int a[50]={0,2,4,8,16,32,64,128,256,
512,1024,2048,4096,8192,16384,
32768,65536,131072,262144,
524288,1048576,2097152,
4194304,8388608,16777216};//小于数据范围的所有2^i的数 
int main()
{
	cin>>n;
	if(n&1)
	{
		cout<<-1;
		return 0;
	}//如果是奇数直接输出-1 
	for(int i=24;i>=1;i--)
	{
		if(n>=a[i])
		{
			n-=a[i];
			cout<<a[i]<<" ";
		}
		if(n==0)//如果n为0了就说明已经被分解完了，无需继续搜索，直接return 0 
		{
			return 0;
		}
	}
} 
