```cpp
#include <bits/stdc++.h>
using namespace std;
inline int read()
{
    int w = 0,f = 1;
    char ch = getchar();
    while(ch < '0' || ch > '9')
    {
        if(ch == '-')    f = -1;
        ch = getchar();
    }
    while(ch >= '0' && ch <= '9')
    {
        w = (w << 3) + (w << 1) + ch - 48;
        ch = getchar();
    }
    return w * f;
}
int a[50];
int main()
{
    int n = read() , num = 0;
    if(n % 2 != 0)    printf("-1\n");
    else
    {
        while(n != 0)
        {
            a[++num] = n % 2;
            n /= 2;
        }
        for(int i = num; i >= 1; i--)
        {
            if(a[i] == 1)    printf("%d " , int(pow(2 , i - 1)));
        }
    }
    return 0;
}
