	char ch=getchar();
	while(!(ch>='0'&&ch<='9')){
		if(ch=='-')f=-1;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9'){
		w=(w<<1)+(w<<3)+(ch-48);
		ch=getchar();
	}
	return w*f;
}//所以事实上并没有用 read() 来读入（

struct Node{
	int val,fa,left,right,size,num;
	Node(int v,int f,int ll,int rr,int sz,int nu):val(v),fa(f),left(ll),right(rr),size(sz),num(nu){}
	Node(){}
};

int cnt=0;
struct BST{

	Node d[MAXN<<1];

	inline void PreFix(){
		for(int i=0;i<MAXN*2;i++)d[i]=Node(0,0,0,0,0,0);
	}

	inline void ins(int x,int o){
		d[o].size++;
		if(d[o].val>x){
			if(d[o].left==0){
				d[++cnt]=Node(x,o,0,0,1,1);
				d[o].left=cnt;
				return ;
			}
			ins(x,d[o].left);
		}
		else if(d[o].val==x){
			d[o].num++;
			return ;
		}
		else{
			if(d[o].right==0){
				d[++cnt]=Node(x,o,0,0,1,1);
				d[o].right=cnt;
				return ;
			}
			ins(x,d[o].right);
		}
	}

	inline int kth(int k,int o){
		if(d[d[o].right].size+d[o].num>=k&&d[d[o].right].size<k){
			return d[o].val;
		}
		else if(d[d[o].right].size+d[o].num<k){
			return kth(k-d[d[o].right].size-d[o].num,d[o].left);
		}
		else if(d[d[o].right].size>=k){
			return kth(k,d[o].right);
		}
	}

};//二叉搜索树，不懂的话左转模板区qwq

BST tree;

int n,w;

int calc(int t,int per){
	int z=t*per;
	int y=z%100;
	z-=y;
	z/=100;//取百位
	//这里也可以直接利用 int 的特性除一下，直接下取整
	return max(z,1);
}

int main(void){

//	freopen("live.in","r",stdin);
//	freopen("live.out","w",stdout);

	scanf("%d%d",&n,&w);

	tree.PreFix();

	for(int i=1;i<=n;i++){
		int x;
		scanf("%d",&x);
		if(i==1){
			tree.d[++cnt]=Node(x,0,0,0,1,1);
			printf("%d ",x);
			continue;
		}//特判一下树内没有节点的情况
		//当然直接在前面搞一个值为 2147483647 的根节点也是没问题的。
		tree.ins(x,1);//插入
		int p=calc(i,w);
		printf("%d ",tree.kth(p,1));//第 k 大
	}

	return 0;
}
```

