int main(){
	cin >> n >> p;
	for (int i=1;i<=n;i++){
		int tot=p*i/100;
		tot=max(1,tot);
		//cout << tot << endl;
		int temp;
		cin >> temp;
		b.push(temp);
		if (a.size()<tot){
			a.push(b.top());
			b.pop();
		}
		else{
			if (a.top()<b.top()){
				a.push(b.top());
				b.pop();
				b.push(a.top());
				a.pop();
			}
		}
		cout << a.top() << " "; 
	}
	return 0；
}
