	cin >> n >> w;
	for(int i=1;i<=n;i++){
		int k = max(1,i*w/100);//获奖人数 
		cin >> p;
		a[p]++;//拿到该分数的人加一 
		x = max(x,p);//目前的最高分，下面遍历时可以不用每次都600次 
		for(int i = x;i >= 0;i--){
