	for(int i=m-1; i>=1; i--)
	{
		for(int j=1; j<=n; j++)
		{
			long long t;
			if(f[1][j][i+1]<f[2][j][i+1]) t=f[2][j][i+1];
			else t=f[1][j][i+1];
			if(f[0][j][i+1]<t) f[0][j][i]=t+a[j][i];
			else f[0][j][i]=f[0][j][i+1]+a[j][i];//推f[0][j][i]
		}
		for(int j=1; j<=n; j++)
		{
			work(1, j, i);//f[1][j][i]
