int ru1[N], ru2[N];
long long val[N];
int sf[N];
long long mul[N];
vector<int>b[N];
void Top1()
{
	queue<int>q;
	for (int i = 1; i <= m; ++ i)
	{
		if(ru1[i] == 0)
		q.push(i);
	}
	while(!q.empty())
	{
		int now = q.front();
		q.pop();
		for (int i = _front[now]; i; i = _eg[i].from)
		{
			int to = _eg[i].to;
			mul[to] *= mul[now];
			mul[to] %= MOD;
			-- ru1[to];
			if(ru1[to] == 0)
			q.push(to);
		}
	}
}
void Top2()
{
	queue<int>q;
	for (int i = 1; i <= m; ++ i)
	{
		if(ru2[i] == 0)
		q.push(i);
	}
	while(!q.empty())
	{
		int now = q.front();
		q.pop();
		for (int i = front[now]; i; i = eg[i].from)
		{
			int to = eg[i].to;
			-- ru2[to];
			val[to] += val[now] * eg[i].w % MOD;
			val[to] %= MOD;
			if(ru2[to] == 0)
			q.push(to);
		}
	}
}
int que[N];
void dfs(int o)
{
	if(sf[o] == 1)
	return;
	sf[o] = 1;
	for (int i = front[o]; i; i = eg[i].from)
	{
		int to = eg[i].to;
		dfs(to);
	}
}
int main()
{
	scanf("%d", &n);
	for (int i = 1; i <= n; ++ i)
	{
		scanf("%lld", &a[i]);
	}
	scanf("%d", &m);
	for (int i = 1; i <= m; ++ i)
	{
		int op;
		scanf("%d", &op);
		t[i] = op;
		if(op == 1)
		{
			scanf("%d%lld", &pos[i], &v[i]);
		}
		if(op == 2)
		{
			scanf("%lld", &v[i]);
		}
		if(op == 3)
		{
			int g;
			scanf("%d", &g);
			for (int j = 1; j <= g; ++ j)
			{
				int x;
				scanf("%d", &x);
				b[i].push_back(x);
				add(i, x);
			}
		}
	}
	num = 0;
	scanf("%lld", &q);
	for (int i = 1; i <= q; ++ i)
	{
		int x;
		scanf("%lld", &x);
		que[i] = x;
		dfs(x);
	}
	for (int i = 1; i <= m; ++ i)
	{
		mul[i] = 1;
		if(t[i] == 2)
		mul[i] = v[i];
		for (int j = front[i]; j; j = eg[j].from)
		{
			int to = eg[j].to;
			if(t[i] != 1)
			{
				add1(to, i);
				++ ru1[i];
			}
		}
	}
	Top1();
	memset(front, 0, sizeof(front));
	memset(eg, 0, sizeof(eg));
	for (int now = 1; now <= m; ++ now)
	{
		if(sf[now] == 0)continue;
		long long mu = 1;
		for (int i = b[now].size() - 1; i >= 0; -- i)
		{
			int to = b[now][i];
			++ ru2[to];
			_add(now, to, mu);
			mu *= mul[to];
			mu %= MOD;
		}
	}
	long long mu = 1;
	for (int i = q; i >= 1; -- i)
	{
		(val[que[i]] += mu) %= MOD;
		mu *= mul[que[i]];
		mu %= MOD;
	}
	Top2();
	for (int i = 1; i <= n; ++ i)
	{
		a[i] *= mu;
		a[i] %= MOD;
	}
	for (int i = 1; i <= m; ++ i)
	{
		if(t[i] == 1)
		a[pos[i]] += val[i] * v[i] % MOD, a[pos[i]] %= MOD; 
	}
	for (int i = 1; i <= n; ++ i)
	{
		printf("%lld ", a[i]);
	}
}
