		scanf("%d %d %d %d",&p[i].a,&p[i].b,&p[i].c,&p[i].d);
	sort(p+1,p+1+n,cmp1);
	int resa=0,ansa=0;
	for(int i=1;i<=n;i++)
	{
		if(resa<p[i].a)
			ansa+=p[i].a-resa,resa=p[i].a;
		if(resa<=1e9)
			resa+=p[i].c;
	}
	printf("%d ",ansa);
	resa=ansa;
	int resb=0,ansb=0;
	for(int i=1,j=0;i<=n;i++)
	{
		while(p[j+1].a<=resa&&j<n)
			j++,h.push(p[j]);
		if(resb<h.top().b)
			ansb+=h.top().b-resb,resb=h.top().b;
		if(resa<=1e9)
			resa+=h.top().c;
		if(resb<=1e9)
