```
#include <bits/stdc++.h>
#define int long long
using namespace std;
struct hehe{
    int a, b, c, d;
    bool operator < (hehe y) const
    {
        return b > y.b;
    }
}a[100001];
int n;
bool cmp(hehe x, hehe y)
{
    if(x.a == y.a)
    {
        return x.b < y.b;
    }
    return x.a < y.a;
}
bool check(int qwq)
{
    for(int i = 1; i <= n; i++)
    {
        if(qwq < a[i].a) return 0;
        qwq += a[i].c;
    }
    return 1;
}
int get_ans(int qwq)
{
    int ret = 0, sum = 0;
    priority_queue<hehe> q;
    int i = 1;
    while(a[i].a <= qwq && i <= n)
    {
        q.push(a[i]);
        i++;
    }
    while(q.size())
    {
        hehe now = q.top();
        q.pop();
        ret = max(ret, now.b - sum);
        sum += now.d;
        qwq += now.c;
        while(a[i].a <= qwq && i <= n)
        {
            q.push(a[i]);
            i++;
        }
    }
    return ret;
}
signed main()
{
    // freopen("forever4.in", "r", stdin);
    int qwqwqwq;
    cin >> qwqwqwq;
