int trans(char ch){
	if(ch>='0'&&ch<='9')return ch-'0';
	if(ch>='A'&&ch<='F')return ch-'A'+10;
	return -1;
}
int read(){
	int res;char ch=getchar();
	while(trans(ch)==-1)ch=getchar();
	while(trans(ch)!=-1)res=(res*16)+trans(ch),ch=getchar();
	return res;
}
void print(int x,int k){
	if(k==6)return ;
	print(x/16,k+1);
	x%=16;
	if(x<10)putchar(x+'0');
	else putchar(x-10+'A');
	return;
