void GenPower() {
  _power[0][0][0] = 1;
  for (int i = 1; i < 12; i++) {
    _power[0][0][i] = _power[0][0][i-1]*5ull;
  }
  for (int i = 1; i < 12; i++) {
    for (int m = 0; m < 12; m++) {
      _power[0][i][m] = _power[0][i-1][m]*3ull;
    }
  }
  for (int i = 1; i < 23; i++) {
