      for (int k = 0; k < 12; k++) {
	_power[i][m][k] = _power[i-1][m][k]*2ul;
	//cout << i << ',' << m << ',' << k << ',' << _power[i][m][k] << endl;
      }
    }
  }
}

void DisplayDiv(Gj a) {
