看到其他人都用拓扑，我默默写了个 dfs。

思路：就是对于每个入水口从这个口遍历到每个出水口。

时间复杂度 $O(nm)$

AC 代码（此代码用了 __int128，考场上我还是只开了 long long） 
```cpp
#include <cstdio>
#include <iostream>
#include <vector>
using namespace std;
vector<int> q[100010];
int n, m, ru[100010], zz[100010], tot1, tot2;
__int128 ans1[100010], ans2[100010];
__int128 gcd(__int128 a, __int128 b) {
	return (b == 0 ? a : gcd(b, a % b));
}
void dfs(int u) {
	int cntt = q[u].size();
	__int128 xx = ans1[u], yy = ans2[u];
	for(int ik = 0; ik < q[u].size(); ik ++) {
		int v = q[u][ik]; 
		__int128 cnt1 = ans1[v], cnt2 = ans2[v], cnt3 = xx, cnt4 = yy * cntt;
		__int128 sto = cnt2 * cnt4 / gcd(cnt2, cnt4);
		cnt1 *= sto / cnt2;
		cnt3 *= sto / cnt4;
		cnt1 += cnt3;
		cnt2 = sto;
		__int128 orz = gcd(cnt1, cnt2);
		cnt1 /= orz;
		cnt2 /= orz;
		ans1[v] = cnt1;
		ans2[v] = cnt2;
		ans1[u] = 0;
		ans2[u] = 1;
		dfs(v);
	}
	return;
}
int read() {
	int num = 0, f = 1;
	char ch = getchar();
	while(ch < '0' || ch > '9') {
		f = (ch == '-' ? -1 : f);
		ch = getchar();
	}
	while(ch >= '0' && ch <= '9') {
		num = (num << 3) + (num << 1) + (ch ^ 48);
		ch = getchar();
	}
	return num * f;
}
void print(__int128 n) {
    if(n > 9) print(n / 10);
    putchar(n % 10 + 48);
}
int main() {
	//freopen("water.in", "r", stdin);
	//freopen("water.out", "w", stdout);
	n = read(), m = read();
	for(int i = 1; i <= n; i ++) {
		int _ = read();
		if(_ == 0) zz[++ tot2] = i;
		for(int j = 1; j <= _; j ++) {
			int vv = read();
			q[i].push_back(vv);
			++ ru[vv];
		}
	}
	for(int i = 1; i <= n; i ++)
		ans2[i] = 1;
	for(int i = 1; i <= n; i ++)
		if(ru[i] == 0)
			ans1[i] = 1, dfs(i);
	for(int i = 1; i <= tot2; i ++)
		print(ans1[zz[i]]), putchar(' '), print(ans2[zz[i]]), putchar('\n');
	return 0;
}
