附上代码：

```cpp
#include<iostream>
#include<cstdio>
#include<algorithm>
#include<queue>
using namespace std;
#define int unsigned long long
const int NR=3e5+10;
struct edge{
	int to,next;
}g[NR<<3];
int tot;
int fte[NR];
void add(int x,int y){
	g[++tot]=(edge){y,fte[x]};
	fte[x]=tot;
}
int gcd(int x,int y){
	if(!x||!y)return x+y;
	return gcd(y,x%y);
}
int lcm(int x,int y){
	return x*y/gcd(x,y);
}
struct task{
	int x,y;
	void init(){x=0;y=1;}
	task(int x1=0,int y1=1){x=x1;y=y1;}
}a[NR];
void merge(task &T){
	int num=gcd(T.x,T.y);
	T.x/=num;T.y/=num;
}
task operator +(const task &x,const task &y){
	task res;res.init();
	res.y=lcm(x.y,y.y);
	res.x=res.y/x.y*x.x+res.y/y.y*y.x;
	merge(res);return res;
}
int cnt[NR];
int sum[NR];
signed main(){
	//freopen("water.in","r",stdin);
	//freopen("water.out","w",stdout);
	int n,m;
	scanf("%llu%llu",&n,&m);
	for(int i=1;i<=n;i++)a[i].init();
	for(int i=1;i<=m;i++)a[i]=a[i]+task(1,1);
	for(int i=1;i<=n;i++){
		scanf("%llu",&cnt[i]);
		for(int j=1;j<=cnt[i];j++){
			int x;
			scanf("%llu",&x);
			add(i,x);sum[x]++;
		}
	}
	queue<int>q;
	for(int i=1;i<=n;i++)if(!sum[i])q.push(i);
	while(!q.empty()){
		int x=q.front();
		q.pop();
		if(!cnt[x])continue;
		a[x].y*=cnt[x];
		for(int i=fte[x];i;i=g[i].next){
			int y=g[i].to;
			a[y]=a[y]+a[x];
			sum[y]--;
			if(!sum[y])q.push(y);
		}
	}
	for(int i=1;i<=n;i++)if(!cnt[i])printf("%llu %llu\n",a[i].x,a[i].y);
	return 0;
}
