#include<bits/stdc++.h>
using namespace std;
const int maxn=100005;
#define ll long long
ll gcd(ll x,ll y)
{
	if(x==0) return y;
	return gcd(y%x,x);
}
struct frac{
	ll x,y;// x/y
	void init()
	{
		x=0,y=1;
	}
	void yf()
	{
		int gc=gcd(x,y);
		x/=gc;
		y/=gc;
	}
	void div(int dd)
	{
		y*=dd;
		yf();
	}
	frac operator +(const frac& p) const{
		frac zz;
		zz.x=x*p.y+y*p.x;
		zz.y=p.y*y;
		zz.yf();
		return zz;
	}
};
int n,m;
int in_deg[maxn];
frac b[maxn];
frac lazy[maxn];
vector<int> en;
vector<int> g[maxn];
queue<int> q;
void link(int ta,int tb)
{
	g[ta].push_back(tb);
}
void flow(int u)
{
	if(!g[u].size()) return;
	lazy[u].div(g[u].size());
	for(int i=0;i<g[u].size();i++) 
	{
		in_deg[g[u][i]]--;
		if(!in_deg[g[u][i]]) q.push(g[u][i]);
		lazy[g[u][i]]=lazy[g[u][i]]+lazy[u];
	}
}

int main()
{
	freopen("water.in","r",stdin);
	freopen("water.out","w",stdout);
	scanf("%d%d",&n,&m);
	for(int i=1;i<=n;i++) lazy[i].init();
	frac ss;
	ss.x=1;
	ss.y=1;

	for(int i=1;i<=m;i++) q.push(i),lazy[i]=ss;
	memset(in_deg,0,sizeof(in_deg));

	int ta,tb;
	for(int i=1;i<=n;i++)
	{
		scanf("%d",&ta);
		if(ta==0) en.push_back(i);
		while(ta--) 
		{
			scanf("%d",&tb);
			link(i,tb);
			in_deg[tb]++;
		}
	}
	while(!q.empty())
	{
		int u=q.front();q.pop();
		flow(u);
	}
	int cnt=0;
	for(int i=0;i<en.size();i++)
	{
		printf("%lld %lld\n",lazy[en[i]].x,lazy[en[i]].y);
	}
	return 0;
}

