倒序建树，从终点往起点 dfs，遇到已确定污水量的点就回去更新。

考场代码（无高精）：

```cpp
#include<bits/stdc++.h>

using namespace std;

typedef long long ll;

void rd(int&x){
	x=0;
	char ch=getchar();
	while(!isdigit(ch)){
		ch=getchar();
	}
	while(isdigit(ch)){
		x=x*10+ch-'0';
		ch=getchar();
	}
}

ll gcd(ll a,ll b){
	return !b?a:gcd(b,a%b);
}

const int maxn=100005;
const int maxm=maxn*5;
int n,m;
int d[maxn];
int tot;
int hd[maxn];
struct Edge{
	int to,nxt;
}edge[maxm];
struct Node{
	ll x;
	ll y;
}wt[maxn];

void adde(int x,int y){
	edge[++tot].to=y;
	edge[tot].nxt=hd[x];
	hd[x]=tot;
}

void initwt(){
	for(int i=1;i<=n;i++){
		wt[i].y=1;
	}
	for(int i=1;i<=m;i++){
		wt[i].x=1;
	}
}

void addwt(int to,int frm){
	//cout<<frm<<' '<<to<<' '<<wt[frm].x<<' '<<wt[frm].y<<' '<<wt[to].x<<' '<<wt[to].y<<endl;
	ll x=wt[frm].x;
	ll y=wt[frm].y*d[frm];
	wt[to].x=x*wt[to].y+wt[to].x*y;
	wt[to].y*=y;
	ll g=gcd(wt[to].x,wt[to].y);
	wt[to].x/=g;
	wt[to].y/=g;
	//cout<<frm<<' '<<to<<' '<<wt[frm].x<<' '<<wt[frm].y<<' '<<wt[to].x<<' '<<wt[to].y<<endl<<endl;
}

void dfs(int u,int f){
	for(int i=hd[u];i;i=edge[i].nxt){
		int o=edge[i].to;
		if(o==f){
			continue;
		}
		if(o<=m||wt[o].x>0){
			addwt(u,o);
			continue;
		}
		dfs(o,u);
		addwt(u,o);
	}
}

