	freopen("water.in","r",stdin);
	freopen("water.out","w",stdout);
	rd(n);
	rd(m);
	for(int i=1;i<=n;i++){
		rd(d[i]);
		for(int j=1;j<=d[i];j++){
			int a;
			rd(a);
			adde(a,i);
		}
	}
	initwt();
	for(int i=1;i<=n;i++){
		if(d[i]==0){
			//cout<<"---"<<endl;
			dfs(i,0);
		}
	}
	for(int i=1;i<=n;i++){
		if(d[i]==0){
			cout<<wt[i].x<<' '<<wt[i].y<<endl;
		}
	}
	return 0;
}
