	memset(v,-1,sizeof(v));	
	bfs2();
	for(int i=1;i<=n;i++)
		for(int j=1;j<=n;j++)
			if(v[i][j]!=-1){
				v[i][j]/=d;
				if(mark[i][j]) v[i][j]--;
			}
	bfs3();
	int res=0;
	for(int i=1;i<=n;i++)
		for(int j=1;j<=n;j++)
			res+=vis[i][j];
	printf("%d\n",res);
	return 0;
}
