```cpp
#include<queue>
#include<cstdio>
#include<cstring>
#define N 1005
using namespace std;
struct node
{
	int x,y,dis;
};
struct node1
{
	int x,y,dis;
	friend bool operator <(const node1 &X,const node1 &Y) {return X.dis<Y.dis;}
};
queue<node> q_rock,q_robot;
priority_queue<node1> getans;
int n,d,ans,fx[5]={0,1,-1,0,0},fy[5]={0,0,0,1,-1},a[N][N],torock[N][N],frorobot[N][N];
bool bj[N][N];
char s[N];
int main()
{
	memset(torock,-1,sizeof(torock));
	memset(frorobot,-1,sizeof(frorobot));
	scanf("%d%d",&n,&d);
	for (int i=1;i<=n;++i)
	{
		scanf("%s",s+1);
		for (int j=1;j<=n;++j)
		{
			if (s[j]=='#')
			{
				a[i][j]=1;
				node x;
				x.x=i;x.y=j;x.dis=0;
				q_rock.push(x);
				torock[i][j]=0;
			}
			if (s[j]=='S')
			{
				a[i][j]=1;
				node x;
				x.x=i;x.y=j;x.dis=0;
				q_robot.push(x);
				frorobot[i][j]=0;
			}
		}
	}
	while (!q_rock.empty())
	{
		node u=q_rock.front();q_rock.pop();
		for (int i=1;i<=4;++i)
		{
			int xx=u.x+fx[i],yy=u.y+fy[i];
			if (torock[xx][yy]<0&&xx>=1&&yy>=1&&xx<=n&&yy<=n)
			{
				torock[xx][yy]=u.dis+1;
				node x;
				x.x=xx;x.y=yy;x.dis=u.dis+1;
				q_rock.push(x);
			}
		}
	}
	while (!q_robot.empty())
	{
		node u=q_robot.front();q_robot.pop();
		if (u.dis/d>=torock[u.x][u.y]) continue;
		for (int i=1;i<=4;++i)
		{
			int xx=u.x+fx[i],yy=u.y+fy[i];
			if (frorobot[xx][yy]<0&&u.dis/d<torock[xx][yy]&&!a[xx][yy])
			{
				frorobot[xx][yy]=u.dis+1;
				node x;
				x.x=xx;x.y=yy;x.dis=u.dis+1;
				q_robot.push(x);
			}
		}
	}
	for (int i=1;i<=n;++i)
		for (int j=1;j<=n;++j)
			if (frorobot[i][j]>=0)
			{
				node1 x;
				x.x=i;x.y=j;x.dis=min(frorobot[i][j]/d,torock[i][j]-1);
				getans.push(x);
				bj[i][j]=true;
				++ans;
			}
	while (!getans.empty())
	{
		node1 u=getans.top();getans.pop();
		if (!u.dis) continue;
		for (int i=1;i<=4;++i)
		{
			int xx=u.x+fx[i],yy=u.y+fy[i];
			if (!bj[xx][yy]&&!a[xx][yy])
			{
				bj[xx][yy]=true;
				node1 x;
				x.x=xx;x.y=yy;x.dis=u.dis-1;
				getans.push(x);
				++ans;
			}
		}
	}
	printf("%d\n",ans);
	return 0;
}
