	}
	rep ( h, 1, m ) {
		int ( *fcur )[MAXN * 2 + 5]( f[h] );
		int ( *flas )[MAXN * 2 + 5]( f[h - 1] );
		rep ( i, 1, n + q ) rep ( j, 1, n + q ) fcur[i][j] = flas[i][j];
		rep ( k, 1, n ) {
