			}
			rep ( i, 1, n ) rep ( j, 1, n + q ) if ( adj[k][i] ) {
				addeq( rig[j], flas[i][j] );
			}
			rep ( i, 1, n + q ) rep ( j, 1, n + q ) {
				addeq( fcur[i][j], mul( lef[i], rig[j] ) );
			}
		}
	}
	rep ( i, 1, q ) printf( "%d\n", f[m][n + i][n + i] );
	return 0;
}

