using namespace std;
const int N = 60;
const int mod = 1e9 + 7;
int n,K,q;
struct Matrix
{
	int a[N + 5][N + 5];
	inline Matrix(int op = 0)
	{
		if(!op)
			for(int i = 1;i <= n;++i)
				for(int j = 1;j <= n;++j)
					a[i][j] = 0;
		else
			for(int i = 1;i <= n;++i)
				for(int j = 1;j <= n;++j)
					a[i][j] = i == j;
	}
	inline int *operator[](int x)
	{
		return a[x];
	}
	inline const int *operator[](int x) const
	{
		return a[x];
	}
	inline Matrix operator*(int x) const
	{
		Matrix ret;
		for(int i = 1;i <= n;++i)
			for(int j = 1;j <= n;++j)
				ret[i][j] = (long long)x * a[i][j] % mod;
		return ret;
	}
	inline Matrix operator+(const Matrix &o) const
	{
		Matrix ret;
		for(int i = 1;i <= n;++i)
			for(int j = 1;j <= n;++j)
