	for(int i=0;i<=len-1;i++)f[s[i]-'0']++;//统计每位数字出现的次数  
	for(int i=9;i>=1;i--)x+=f[i]*i;//算数字和 
	if(x%3||!f[0])cout<<-1;//假如不是30的倍数  
