	return a>b;
}
int main(){
	cin>>str;
	F(i,0,str.size()-1){
		tot+=(str[i]-'0');
		if(str[i]=='0')f=1;
	}
	if(f==0||tot%3){
		puts("-1");
		exit(0);
	}
	sort(str.begin(),str.end(),cmp);
	cout<<str<<endl;
    return 0;
