	scanf("%d",&n);
	a[1] = n % 10;
	a[2] = n % 100 / 10;
	a[3] = n % 1000 / 100;
	a[4] = n % 10000 / 1000;
	a[5] = n % 100000 / 10000;
	a[6] = n / 100000;
	for (int i=6; i>=1; i-- ) {
		if (a[i]) {
			cnt = i;
			break;
		}
	}
	for (int i=1; i<=cnt; i++ ) {
		if (a[i] == 0) {
			flag = 1;
		}
		sum += a[i];
	}
	if (!flag) {
		printf("-1");
		return 0;
	}
	else if (sum % 3 == 0){
		std::stable_sort(a + 1 , a + cnt + 1);
		for (int i=cnt; i>=1; i-- ) {
			printf("%d",a[i]);
		}
	}
}
```
