##  你们的最爱

```
#include<bits/stdc++.h>
using namespace std;
char n[100004];
int k,sum;
bool qwq(char x,char y){
	return x>y;//将每位数字从大到小排序
}
int main(){
	cin>>n;
	for(int i=0;i<strlen(n);i++){
		if(n[i]=='0')
			k=1;//判断该数是否含0
		else
			sum+=n[i]-'0'; //计算每位数字的和
	}
	if(k==0||sum%3!=0){
		cout<<-1;
		return 0;
	}//判断是否符合思路一
	sort(n,n+strlen(n),qwq);
	cout<<n;//输出
	return 0;
}
