			for(int i = 1;i<n;++i) ret = min(ret,dfs(i,m,w,a,0,d)+dfs(n-i,m,0,a,s,d));
		}
	}else return f[n][m][w][a][s][d];
	return f[n][m][w][a][s][d] = ret; 
