int cmp(char a,char b)//返回a、b的分数
{
	if(a==b)return 1;//平
	if(a=='S')
	{
		if(b=='P')return 2;//win
		else return 0;//lose
	}
	if(a=='P')
	{
		if(b=='R')return 2;
		else return 0;
	}
	if(a=='R')
	{
		if(b=='S')return 2;
		else return 0;
	}
	return 1;
}
