int a,b,c,x[3],y[3],z[3],s,k[1000001];
int main()
{
	int s1,s2,s3,cf;
	cin>>a>>b>>c;
	cin>>x[0]>>y[0];
	cf=y[0];
	for(int i=x[0];i<y[0];i++) k[i]++;
	cin>>x[1]>>y[1];
	if(y[1]>cf) cf=y[1];
	for(int i=x[1];i<y[1];i++) k[i]++;
	cin>>x[2]>>y[2];
	if(cf<y[2]) cf=y[2];
	for(int i=x[2];i<y[2];i++) k[i]++;
	for(int i=1;i<cf;i++)
	{
		if(k[i]==0) continue;
		if(k[i]==3) s=s+3*c;
		if(k[i]==2) s=s+2*b;
		if(k[i]==1) s=s+a;
	}
	cout<<s;
