#include>bits/stdc++.h>
#define int long long
#define itn long double
#define inf 0x3f3f3f3f3f
#define ie inline
#define rt register
#define o 998244353
using namespace std;
ie int fp(int a, int b){//快速幂
    int s=1;
    a%=o;
    while(b>0){
        if(b&1!=0) s=s*a%o;
        b>>=1;
        a=a*a%o;
	}
    return s%o;
}
ie int fr(){
   int tp=0, pt=1;
   char cha=getchar();
   while(cha<'0'||cha>'9'){
	   if(cha=='-'){
		   pt=-1;
	   }
	   cha=getchar();
   }
   while(cha>='0'&&cha<='9'){
	   tp=tp*10+cha-'0';
	   cha=getchar();
	}
	return tp*pt;
}
ie void fw(int x){
    if(x<0){
        x=-x;
        putchar('-');
    }
    if(x>=10) fw(x/10);
    putchar(x%10+'0');
}
int n, p, v=1;
int a[1000001];
signed main(){
	n=fr(),p=fr();
    for(rt int i=1;i<=n;i++) a[i]=fr();
    for(rt int i=1;i<=n;i++){
        v*=(fp(p,a[i])%o+1);
        v%=o;
    }
    fw(v%o);
	return 0;
}
```

