int main  ( ) { 
  int t; cin >> t; 
  while(t  --) { 
    int n, cnt=0, tmp; cin >> n; 
    for(int i = 1; i <= n;  i++ ) {
      scanf("%d",&tmp);if(tmp==0)cnt++; 
    }
    printf("%d\n",n-cnt);
    for(int i=1;i<=n;i++)printf("1 ");puts(""); 
  } 
}
