		for(int i=1;i<=n;i++) cin>>a[i];
		for(int i=1;i<=n;i++){
			if(a[i]==0) ans++;
		}
		cout<<n-ans<<endl;
		for(int i=1;i<=n;i++) cout<<1<<" ";
