        bool in[100001]={0};
        cin>>n;
        for (int i=1;i<=n;i++){
            cin>>in[i];
            if (in[i]==0) ++s;
        }
        cout<<n-s<<endl; //上文已经解释
        for (int i=1;i<=n;i++) cout<<1<<" ";  //注意空格
    }
    return 0;
}
