	for(int i=1;i<=n;i++)scanf("%lld",&num[i]);
	for(int i=0;i<=30;i++){
		l=0;r=2e9;t=1<<i;
		while(l<=r){
			mid=(l+r)>>1;
			if(check(mid,s/t+(s%t!=0))){
				if(ans==-1)ans=mid*a+i*b;
				ans=min(ans,mid*a+i*b);
				r=mid-1;
			}
			else l=mid+1;
		}
	}
	printf("%lld",ans);
