int n,K,m;
inline void add(int x){magius+=!(cnt[x]++);}
inline void del(int x){magius-=!(--cnt[x]);}
inline void count(int u)
{
	if(vis[u])
		return;
	vis[u]=1,now.pb(u);
	if((int)S[u].size())
		count(S[u].rbegin()->fi);
}
signed main(void)
{
	read(n,K,m);
	for(int i=1;i<=n;++i)
		from[i]=i,S[i].pb(MP(i,0));
	reg int x,y;
	for(int i=1;i<=K;++i)
		read(x,y),S[from[x]].pb(MP(y,i)),S[from[y]].pb(MP(x,i)),
		std::swap(from[x],from[y]);
	for(int i=1;i<=n;++i)
	{
		if(vis[i])
			continue;
		now.clear(),count(i);
		reg int P=m/K,Q=m%K;
		if(P>=(int)now.size())
			P=(int)now.size(),Q=0;
		reg int posR=P-1;
		for(int j=0;j<=posR;++j)
			for(int k=0;k<(int)S[now[j]].size()&&S[now[j]][k].se<=K;++k)
				add(S[now[j]][k].fi);
		for(int j=0;j<(int)now.size();++j)
		{
			posR=(posR+1)%(int)now.size();
			for(int k=0;k<(int)S[now[posR]].size()&&S[now[posR]][k].se<=Q;++k)
				add(S[now[posR]][k].fi);
			ans[now[j]]=magius;
			for(int k=0;k<(int)S[now[posR]].size()&&S[now[posR]][k].se<=Q;++k)
				del(S[now[posR]][k].fi);
			for(int k=0;k<(int)S[now[j]].size()&&S[now[j]][k].se<=K;++k)
				del(S[now[j]][k].fi);
			for(int k=0;k<(int)S[now[posR]].size()&&S[now[posR]][k].se<=K;++k)
				add(S[now[posR]][k].fi);
		}
		for(int j=0;j<(int)now.size();++j)
			for(int k=0;k<(int)S[now[j]].size()&&S[now[j]][k].se<=K;++k)
				cnt[S[now[j]][k].fi]=0;
		magius=0;
	}
	for(int i=1;i<=n;++i)
		write(ans[i]),ln;
	return 0;
}
