		x = read(), y = read(), z = read(); 
		pie[i] = (PIE){x, y, z};
	}
	sort(pie+1, pie+N+1);
	for(int i=1; i<=N; i++){
		int val = ask(1, -W, W, -W, 2*pie[i].t - pie[i].p) + pie[i].v;
		insert(root, -W, W, 2*pie[i].t - pie[i].p, val);
	}
	cout<<t[1].dat<<endl;
	return 0;
}
