inline int read() {
    int f = 0, s = 0;
    char ch = getchar();
    while (!isdigit(ch)) f |= ch == '-', ch = getchar();
    while (isdigit(ch)) s = s * 10 + (ch ^ 48), ch = getchar();
    return f ? -s : s;
}

void print (int x) {
    if (x < 0) putchar('-'), x = -x;
    if (x > 9) print (x / 10);
    putchar(x % 10 + '0');
}

const int mod = 4;
int n, k, m;

struct matrix {
    int n, m;
    int z[3][3];
    matrix () {
        n = m = 0;
        memset (z, 0, sizeof z);
    }
};

matrix operator*(const matrix &a, const matrix &b) {
    matrix c;
    c.n = a.n;
    c.m = b.m;
    for (int i = 1; i <= c.n; i++)   
        for (int j = 1; j <= c.m; j++) 
            for (int k = 1; k <= a.m; k++) 
                c.z[i][j] = ((c.z[i][j] % mod) + ((a.z[i][k] % mod) * (b.z[k][j]) % mod) % mod);
    return c;
}

matrix mi(matrix a, int b) {
    matrix ans, base = a;
    ans.n = ans.m = a.m;
    for (int i = 1; i <= a.m; i++) ans.z[i][i] = 1;
    while(b > 0) {
        if(b & 1)
            ans = ans * base;
        base = base * base;
        b >>= 1;
    }
    return ans;
}

int qpow(int a, int b) {
	int res = a, ans = 1;
	while(b) {
		if (b & 1) ans = res * ans % 10;
		res = res * res % 10;
		b >>= 1;
	}
	return ans;
}

