struct Number{
	ll val,row,type;
	bool operator<(const Number& n2)const{
		return val>n2.val;
	}
}a[MAXN],b[MAXN];
ll n,w,t1,t2,ans,sum;
void solve(Number* a,int begin,int end,int rd){
	if(begin>end)return;
	int L=begin,R=end;
	while(L<=R){
		if((odd(rd)) == a[L].type){
			//加法环节 
			ans+=(sum+a[L].row);
			sum+=a[L].val;
			L++;
		}else{
			//减法环节 
			ans+=(sum+a[R].row);
			sum-=a[R].val;
			R--;
		}
		rd++;
	}
}
int main(){
	cin>>n;
	rep(i,1,n){
		cin>>w;
		ll tmp=w;
		//计算奇数的时候的值 
		if((odd(abs(2+w)))==1)w=-w;
		if(w>0){
			a[++t1]=(Number){abs(w),tmp,1};
		}else{
			b[++t2]=(Number){abs(w),tmp,0};
		}
	}
	sort(a+1,a+1+t1);
	sort(b+1,b+1+t2);
	ll L=1,R=1,cnt=1;
	while(1){
		if(L>t1)break;
		ans=(ans+sum+a[L].row);	
		sum+=a[L].val;
		L++;
		cnt++;
		if(R>t2)break;
		ans=(ans+sum+b[R].row);
		sum+=b[R].val;
		R++;
		cnt++;
	}
	solve(a,L,t1,cnt);
	solve(b,R,t2,cnt);
	cout<<ans;

	return 0;
}

