#include<stdio.h>
#include<string.h>
#include<math.h>
#include<algorithm>
#include<vector>
#define fr(i,a,b) for(int i=(a),end_##i=(b);i<=end_##i;i++)
#define fd(i,a,b) for(int i=(a),end_##i=(b);i>=end_##i;i--)
#define i64 long long
#include<assert.h>
int read()
{
	int r=0,t=1,c=getchar();
	while(c<'0'||c>'9')
	{
		t=c=='-'?-1:1;
		c=getchar();
	}
	while(c>='0'&&c<='9')
	{
		r=(r<<3)+(r<<1)+(c^48);
		c=getchar();
	}
	return r*t;
}
const int N=100010;
int gcd(int a,int b){ return b?gcd(b,a%b):a; }
void exgcd(int a,int b,int &x,int &y)
{
	if(!b){ x=1; y=0; return ; }
	exgcd(b,a%b,y,x); y-=(a/b)*x;
}
int inv(int a,int p){ int x,y; exgcd(a,p,x,y); return (x%p+p)%p; }
struct Solve
{
	int n;
	std::vector<int> f;
	void dfs(int u)
	{
//		fprintf(stderr,"%d\n",u);
		if(u==0) return ;
		if(u==n-1){ f[u]=1; return ; }
		if(f[u]) return ;
		f[u]=-1;
		if(gcd(u+1,n)!=1)
		{
			// 1 u u+1 2u+1 3u+2
			int v=(i64)(u*3+2)*inv(u*2+1,n)%n;
			dfs(v); if(f[v]==-1) f[u]=-1; else f[u]=f[v]+3;
		}
		else
		{
			int v=(i64)(u+1)*inv(u,n)%n;
			dfs(v); if(f[v]==-1) f[u]=-1; else f[u]=f[v]+1;
		}
	}
	Solve(int _n=0)
	{
		n=_n; if(!n) return ;
		f.resize(n); //fprintf(stderr,"%d\n",n);
		fr(i,1,n-1) if(gcd(n,i)==1) dfs(i);
	}
} s[100];
int n,q,m;
int p[N],c[N],t;
int id[100][100];
void init(int n)
{
	fr(i,2,n) if(n%i==0)
	{
		p[++t]=i;
		while(n%i==0){ n/=i; c[t]++; }
	}
}
int merge(int &a,int &b,int A,int B)
{
	int g=gcd(b,B);
	if(a%g!=A%g) return 0;
	if(b<B)
	{
		fr(i,0,b/g-1) if((A+B*i)%b==a)
		{
			a=A+B*i; b=b*B/g; a%=b; return 1;
		}
	}
	else
	{
		fr(i,0,B/g-1) if((a+b*i)%B==A)
		{
			a=a+b*i; b=b*B/g; a%=b; return 1;
		}
	}
	return 0;
}
int solve(int x,int y)
{
	if(x==0) return 0;
	if(y==0) return 1;
	int a=0,b=1;
	fr(i,1,t)
	{
		int C=c[i],X=x,Y=y;
		while(C&&X%p[i]==0&&Y%p[i]==0){ C--; X/=p[i]; Y/=p[i]; }
		if(!C) continue;
		Solve &S=s[id[i][C]];
		int m=S.n; X%=m; Y%=m; int Ans=1;
		if(X==0)
		{
			if(!merge(a,b,0,S.f[1]+2)) return -1;
			continue;
		}
		while(Y&&(gcd(X,m)!=1||gcd(Y,m)!=1))
		{
			int k=X; X=Y; Y=(Y+k)%m; Ans++;
		}
		if(Y==0)
		{
			if(!merge(a,b,Ans,S.f[1]+2)) return -1;
			continue;
		}
		int u=(i64)Y*inv(X,m)%m;
		if(S.f[u]==-1) return -1;
		if(!merge(a,b,S.f[u]+Ans,S.f[1]+2)) return -1;
		// 1 1 [... 0] 1 1
	}
	return a;
}
int bf(int x,int y)
{
	if(x==0) return 0;
	int ans=1;
	while(y)
	{
		int k=x; x=y; y=(y+k)%n; ans++;
		if(ans>n*n) return -1;
	}
	return ans;
}
int main()
{
//	freopen("fib.in","r",stdin); freopen("fib.out","w",stdout);
	q=read(); n=read(); init(n);
	fr(i,1,t)
	{
		int r=1;
		fr(j,1,c[i])
		{
			r=r*p[i];
			s[id[i][j]=++m]=Solve(r);
		}
	}
	while(q--)
	{
		int x=read(),y=read();
		printf("%d\n",solve(x,y));
//		assert(solve(x,y)==bf(x,y));
	}
	return 0;
}
