	for(int i=1;i<=m+1;i++){
		sort(a+x[i-1]+1,a+x[i]+1);//这里要注意，因为x[0]默认为0，所以i=1时没有问题，前提一定时x[]数组设为全局变量
	}
	for(int i=1;i<=n;i++){
		printf("%lld ",a[i]);
	}
	return 0;
}
