signed main()
{
        cin >> n >> m >> k;
        for (i=1; i<=n; i++) cin >> p[i];
        for (i=0; i<=m; i++) cin >> b[i];
        for (i=1; i<=n; i++)
                for (j=0; j<=k; j++)
                        for (kk=0; kk<=k; kk++)
                                if (p[i]+kk<=m&&j>=kk) f[i][j]=max(f[i][j],f[i-1][j-kk]+b[p[i]+kk]);//转移
        for (i=0; i<=k; i++) ans=max(ans,f[n][i]);
        cout << ans;
}

