int t;
int main(){
	cin>>t;
	while(t--){
		int n,m;

		cin>>n>>m;int k=m;
		bool is=1;
		for(int i=2;i<=n;i++){
			m-=i;
			if(m<=i){//构造失败，有重复 
				is=0;
				break;
			}
		}
		if(is&&m>1){
			for(int i=2;i<=n;i++){//构造成功 
				printf("%d ",i);
			}
			cout<<m<<'\n'<<k-1<<'\n';
		}else{
			cout<<-1<<'\n';
		}

	}
	return 0;
}
