typedef long long ll;
#define For(i,l,r) for (ll i=l,end=r;i<=end;++i)
#define FOR(i,l,r) for (ll i=l,end=r;i>=end;--i)
using namespace std;

void solve(){
    ll n,m;scanf("%lld%lld",&n,&m);
    if ((n+3)*n/2>m){printf("-1\n");return;}
    For(i,2,n) printf("%lld ",i);
    printf("%lld\n%lld\n",m-(n+2)*(n-1)/2,m-1);
} 

int main(){

    ll t;scanf("%lld",&t);
    For(i,1,t) solve(); 
    return 0;
}
