	scanf("%d",&n);
	while(n--){
		scanf("%d",&x);
		while(!s.empty()&&x<s.top())
			s.pop();
		if(!s.empty()&&x==s.top()){
			continue;
		} 
		if(x!=0){
			ans++;
			s.push(x);
		}

	}
	cout<<ans;
	return 0; 
}
```
