	}
	for(int i=1;i<=m;i++)cin>>q[i];
	vi v(n);
	for(int i=1;i<=n;i++) v[i-1]=i;
	solve(1,m+1,v);
	for(int i=1;i<=m;i++)cout<<ans[i]<<'\n';
	return 0;
}
